contracts/token/Sale.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity 0.8.23;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "../interfaces/IERC20Burnable.sol";

contract Sale {

  uint public constant SALE_TOTAL_AMOUNT = 4_000_000e18;
  /// @notice burnNotSold cannot be called if less than 30 days passed since the sale end
  uint public constant MIN_CLAIMING_PERIOD = 30 days;

  address public immutable governance;
  address public immutable payToken;
  uint public immutable price;
  uint public immutable start;
  uint public immutable end;

  address public tokenToSale;
  uint public sold;
  mapping(address user => uint bought) public bought;
  bool public allowToClaim = false;

  constructor(
    address _gov,
    address _payToken,
    uint _price,
    uint _start,
    uint _end
  ) {
    require(_gov != address(0), "zero gov");
    require(_payToken != address(0), "zero pay");
    require(_price > 0, "zero price");
    require(_start > block.timestamp, "incorrect start");
    require(_end > _start + 1 days, "incorrect end");

    governance = _gov;
    payToken = _payToken;
    price = _price;
    start = _start;
    end = _end;
  }

  // anyone can call, assume to be called during the token deploy
  function setupTokenToSale(address token) external {
    require(token != address(0), "zero token");
    require(tokenToSale == address(0), "already");
    require(IERC20(token).balanceOf(address(this)) == SALE_TOTAL_AMOUNT, "incorrect supply");

    tokenToSale = token;
  }

  // after the sale ended we need to allow to claim in the same moment when create the token pool
  function allowClaim() external {
    require(msg.sender == governance, "not allowed");
    allowToClaim = true;
  }

  /// @notice Burn ALL tokens from balance (users won't be able to claim tokens after that)
  /// @dev governance can call after the sale end + 1 month
  function burnNotSold() external {
    require(msg.sender == governance, "not allowed");

    address _token = tokenToSale;
    require(_token != address(0) && block.timestamp > end, 'not ended');
    require(block.timestamp >= end + MIN_CLAIMING_PERIOD, '< 1 month since end');

    uint toBurn = IERC20(_token).balanceOf(address(this));
    require(toBurn != 0, 'nothing to burn');
    IERC20Burnable(_token).burn(toBurn);
  }

  function buy(uint amount) external {
    require(block.timestamp >= start, "Sale is not started yet");
    require(block.timestamp < end, "Sale ended");
    require(sold + amount <= SALE_TOTAL_AMOUNT, "Too much");

    uint totalBought = bought[msg.sender];
    require(totalBought + amount <= SALE_TOTAL_AMOUNT / 10, "Too much for user");

    uint toSpend = amount * price / 1e18;
    require(toSpend > 0, "Zero amount");

    sold += amount;
    IERC20(payToken).transferFrom(msg.sender, governance, toSpend);
    bought[msg.sender] = totalBought + amount;
  }

  function claim() external {
    address _token = tokenToSale;
    require(_token != address(0) && block.timestamp >= end, 'sale not ended');
    require(allowToClaim, 'not allowed yet');

    uint userBought = bought[msg.sender];
    require(userBought > 0, 'bought zero');

    bought[msg.sender] = 0;
    IERC20(_token).transfer(msg.sender, userBought);
  }
}

contracts/token/TokenFactory.sol.content:
// SPDX-License-Identifier: MIT

pragma solidity 0.8.23;

import "../interfaces/IVesting.sol";
import "../interfaces/ISale.sol";
import "@openzeppelin/contracts/utils/Create2.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";

interface IMYRD is IERC20 {
  function MAX_SUPPLY() external view returns (uint);

  function minter() external view returns (address);

  function mint(address to, uint256 amount) external;
}

contract TokenFactory {

  // ********************** TOKENOMICS **********************

  uint private constant PUBLIC_SALE_AMOUNT = 4_000_000e18;
//  uint private constant PUBLIC_SALE_CLIFF = 0;
//  uint private constant PUBLIC_SALE_VESTING = 0;

  uint private constant LIQUIDITY_AMOUNT = 6_000_000e18;
//  uint private constant LIQUIDITY_CLIFF = 0;
//  uint private constant LIQUIDITY_VESTING = 0;

  uint private constant TEAM_AMOUNT = 20_000_000e18;
  uint private constant TEAM_CLIFF = 182 days;
  uint private constant TEAM_VESTING = 1277 days;

  uint private constant TREASURY_AMOUNT = 50_000_000e18;
  uint private constant TREASURY_CLIFF = 365 days;
  uint private constant TREASURY_VESTING = 1095 days;

  uint private constant REWARDS_AMOUNT = 20_000_000e18;
  uint private constant REWARDS_CLIFF = 547 days;
  uint private constant REWARDS_VESTING = 912 days;

  // ********************** VARIABLES **********************

  address public governance;
  IMYRD public token;
  address public saleContract;
  uint public cliffStarted;
  IVesting public vestingContractTeam;
  IVesting public vestingContractTreasury;
  IVesting public vestingContractRewards;
  // vesting contract => start block. Zero means not started yet.
  mapping(address => uint) public vestingStarted;

  function computeAddress(bytes32 salt, bytes memory bytecode) external view returns (address) {
    return Create2.computeAddress(salt, keccak256(bytecode), address(this));
  }

  // need to pass immutable deployed vesting contracts to be sure that final total supply is correct
  function createToken(
    bytes32 salt,
    bytes memory bytecode,
    address _governance,
    address _saleContract,
    address _vestingContractTeam,
    address _vestingContractTreasury,
    address _vestingContractRewards
  ) external {
    require(
      address(token) == address(0)
      && governance == address(0)
      && address(vestingContractTeam) == address(0)
      && address(vestingContractTreasury) == address(0)
      && address(vestingContractRewards) == address(0)
      , 'created');
    require(
      _governance != address(0)
      && _vestingContractTeam != address(0)
      && _vestingContractTreasury != address(0)
      && _vestingContractRewards != address(0)
      , "empty");

    IMYRD _token = IMYRD(Create2.deploy(0, salt, bytecode));
    require(_token.MAX_SUPPLY() == PUBLIC_SALE_AMOUNT + LIQUIDITY_AMOUNT + TEAM_AMOUNT + TREASURY_AMOUNT + REWARDS_AMOUNT, "amounts don't match");

    governance = _governance;
    token = _token;
    saleContract = _saleContract;
    cliffStarted = block.timestamp;

    vestingContractTeam = IVesting(_vestingContractTeam);
    require(IVesting(_vestingContractTeam).vestingPeriod() == TEAM_VESTING, 'team wrong vesting');
    require(IVesting(_vestingContractTeam).cliffPeriod() == 0, 'team wrong cliff');
    require(IVesting(_vestingContractTeam).tgePercent() == 0, 'team wrong tge');

    vestingContractTreasury = IVesting(_vestingContractTreasury);
    require(IVesting(_vestingContractTreasury).vestingPeriod() == TREASURY_VESTING, 'treasury wrong vesting');
    require(IVesting(_vestingContractTreasury).cliffPeriod() == 0, 'treasury wrong cliff');
    require(IVesting(_vestingContractTreasury).tgePercent() == 0, 'treasury wrong tge');

    vestingContractRewards = IVesting(_vestingContractRewards);
    require(IVesting(_vestingContractRewards).vestingPeriod() == REWARDS_VESTING, 'rewards wrong vesting');
    require(IVesting(_vestingContractRewards).cliffPeriod() == 0, 'rewards wrong cliff');
    require(IVesting(_vestingContractRewards).tgePercent() == 0, 'rewards wrong tge');

    require(_token.totalSupply() == 0, "wrong total supply");
    require(_token.minter() == address(this), "wrong gov");

    _token.mint(_saleContract, PUBLIC_SALE_AMOUNT);
    _token.mint(_governance, LIQUIDITY_AMOUNT);

    ISale(_saleContract).setupTokenToSale(address(_token));
  }

  function startTeamVesting(address[] calldata claimants, uint[] calldata amounts) external {
    _startVesting(vestingContractTeam, TEAM_CLIFF, TEAM_AMOUNT, claimants, amounts);
  }

  function startTreasuryVesting(address[] calldata claimants, uint[] calldata amounts) external {
    _startVesting(vestingContractTreasury, TREASURY_CLIFF, TREASURY_AMOUNT, claimants, amounts);
  }

  function startRewardsVesting(address[] calldata claimants, uint[] calldata amounts) external {
    _startVesting(vestingContractRewards, REWARDS_CLIFF, REWARDS_AMOUNT, claimants, amounts);
  }

  function _startVesting(
    IVesting vestingContract,
    uint cliff,
    uint totalAmount,
    address[] calldata claimants,
    uint[] calldata amounts
  ) internal {
    require(msg.sender == governance, "governance");
    require(vestingStarted[address(vestingContract)] == 0, "started");
    require(block.timestamp >= cliffStarted + cliff, "cliff");

    vestingStarted[address(vestingContract)] = block.number;

    IMYRD _token = IMYRD(token);

    _token.mint(address(vestingContract), totalAmount);

    IVesting(vestingContract).start(
      true,
      address(_token),
      totalAmount,
      claimants,
      amounts
    );
  }

}

contracts/token/Vesting.sol.content:
// SPDX-License-Identifier: MIT

pragma solidity 0.8.23;

import "../interfaces/IVesting.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import "@openzeppelin/contracts/utils/math/Math.sol";

/// @title Vesting contract for token distribution
contract Vesting is IVesting, ReentrancyGuard {

  //////////////////////////////

  /// @dev Will start after the cliff
  uint public immutable vestingPeriod;
  /// @dev Delay before the vesting
  uint public immutable cliffPeriod;
  /// @dev Percent of TGE, no decimals
  uint public immutable tgePercent;

  //////////////////////////////

  /// @dev Token for vesting
  IERC20 public token;

  /// @dev Start of vesting
  uint public vestingStartTs;

  /// @dev Claimant => whole amount for distribution
  mapping(address => uint) public toDistribute;
  /// @notice Claimant => [last claimed timestamp, claimed vesting amount]
  /// @dev Use unpackLastVestedData to decode
  mapping(address => bytes32) public lastVestedClaim;
  /// @dev Claimant => TGE claimed indicator
  mapping(address => bool) public tgeClaimed;
  /// @dev if for some reason claimant did not claim all tokens it recorded in this map for make possible to claim it later
  mapping(address => uint) public extraAmounts;

  //////////////////////////////

  event Created(uint vestingPeriod, uint cliffPeriod, uint tgePercent);
  event Started(address token, uint totalAmount, uint time, address[] claimants, uint[] amounts);
  event Claimed(address claimer, uint amount);

  //////////////////////////////

  constructor(uint _vestingPeriod, uint _cliffPeriod, uint _tgePercent) {
    require(_vestingPeriod > 0, "Zero vesting");
    require(_tgePercent < 100, "Too much TGE");

    vestingPeriod = _vestingPeriod;
    cliffPeriod = _cliffPeriod;
    tgePercent = _tgePercent;

    emit Created(_vestingPeriod, _cliffPeriod, _tgePercent);
  }

  //////////////////////////////

  function start(
    bool useTokensOnBalance,
    address _token,
    uint totalAmount,
    address[] calldata claimants,
    uint[] calldata amounts
  ) external override {
    require(vestingStartTs == 0, "Already started");
    require(_token != address(0), "Zero address");
    require(claimants.length == amounts.length, "Wrong input");

    token = IERC20(_token);
    vestingStartTs = block.timestamp + cliffPeriod;

    uint totalForCheck;

    for (uint i; i < claimants.length; ++i) {
      address claimant = claimants[i];
      uint amount = amounts[i];
      require(claimant != address(0), "Zero address");
      require(amount > 0, "Zero amount");

      toDistribute[claimant] = amount;
      totalForCheck += amount;
    }

    require(totalForCheck == totalAmount, "Wrong total amount");

    if (useTokensOnBalance) {
       require(token.balanceOf(address(this)) >= totalAmount, "Not enough tokens");
    } else {
      IERC20(_token).transferFrom(msg.sender, address(this), totalAmount);
    }

    emit Started(_token, totalAmount, block.timestamp, claimants, amounts);
  }


  /// @notice Returns the claimable amount for a given claimant
  /// @param claimant Address of the claimant
  /// @return amount Total amount claimable at current moment
  /// @return _lastVestedClaimTs Timestamp of the last vested claim
  /// @return extraAmount Extra amount that can be claimed (it's included to {amount})
  /// @return amountVestingToClaim The amount of vesting that will be credited as claimed during the next claim call
  /// @return claimedVestingAmount The amount of vesting that is currently considered claimed
  function toClaim(address claimant) public view returns (
    uint amount,
    uint _lastVestedClaimTs,
    uint extraAmount,
    uint amountVestingToClaim,
    uint claimedVestingAmount
  ) {
    uint _vestingStartTs = vestingStartTs;
    (_lastVestedClaimTs, claimedVestingAmount) = unpackLastVestedData(lastVestedClaim[claimant]);
    _lastVestedClaimTs = _lastVestedClaimTs == 0 ? _vestingStartTs : _lastVestedClaimTs;

    if (_lastVestedClaimTs != 0) {
      uint vestingTime = _vestingStartTs + vestingPeriod < block.timestamp ? _vestingStartTs + vestingPeriod : block.timestamp;
      uint timeDiff = _lastVestedClaimTs < vestingTime ? vestingTime - _lastVestedClaimTs : 0;

      uint _toDistribute = toDistribute[claimant];
      uint claimableTGE = _toDistribute * tgePercent / 100;
      uint claimableVesting = _toDistribute - claimableTGE;
      claimableTGE = tgeClaimed[claimant] ? 0 : claimableTGE;

      extraAmount = extraAmounts[claimant];

      // The claimant should claim exactly {_toDistribute} amount in total.
      // Don't allow any losses because of rounding.
      amountVestingToClaim = block.timestamp <= vestingTime
        ? timeDiff * claimableVesting / vestingPeriod
        : claimableVesting > claimedVestingAmount ? claimableVesting - claimedVestingAmount : 0;

      amount = amountVestingToClaim + claimableTGE + extraAmount;

      uint balance = token.balanceOf(address(this));
      amount = balance < amount ? balance : amount;
    } else {
      // not started yet
    }

    return (amount, _lastVestedClaimTs, extraAmount, amountVestingToClaim, claimedVestingAmount);
  }

  function claim() external nonReentrant {
    (uint _toClaim, uint _lastVestedClaimTs, uint extraAmount, uint amountVestingToClaim, uint claimedAmount) = toClaim(msg.sender);

    require(_lastVestedClaimTs != 0, "Not started");
    require(_toClaim != 0, "Nothing to claim");

    // if vesting started need to update last claim timestamp
    if (_lastVestedClaimTs < block.timestamp) {
      lastVestedClaim[msg.sender] = packLastVestedData(uint64(block.timestamp), claimedAmount + amountVestingToClaim);
    }

    // assume that any claim will mark TGE as claimed
    if (!tgeClaimed[msg.sender]) {
      tgeClaimed[msg.sender] = true;
    }

    // if extra amount is not zero need ro reset it
    if (extraAmount != 0) {
      delete extraAmounts[msg.sender];
    }

    uint notClaimed = _transferClaimedTokens(token, _toClaim, msg.sender);

    // if we claimed not all amount need to write extra amount for future claims
    if (notClaimed != 0) {
      extraAmounts[msg.sender] = notClaimed;
    }

    emit Claimed(msg.sender, _toClaim);
  }

  function _transferClaimedTokens(IERC20 _token, uint amount, address claimant) internal virtual returns (uint notClaimed) {
    _token.transfer(claimant, amount);
    // by default all tokens claimed
    return 0;
  }

  function packLastVestedData(uint64 lastVestedClaimTs, uint claimedVestingAmount) internal pure returns (bytes32 data) {
    data = bytes32(uint(lastVestedClaimTs));
    data |= bytes32(uint(uint192(claimedVestingAmount))) << 64;
  }

  function unpackLastVestedData(bytes32 data) public pure returns (uint64 lastVestedClaimTs, uint claimedVestingAmount) {
    lastVestedClaimTs = uint64(uint(data));
    claimedVestingAmount = uint192(uint(data) >> 64);
  }
}

contracts/token/XMyrd.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.23;

import "../openzeppelin/ERC20Upgradeable.sol";
import "../openzeppelin/EnumerableSet.sol";
import "../proxy/Controllable.sol";
import "../interfaces/IControllable.sol";
import "../interfaces/IXMyrd.sol";
import "../interfaces/IAppErrors.sol";
import "../interfaces/IGauge.sol";

/// @title xMyrd token
/// Inspired by XSTBL from Stability codebase
contract XMyrd is Controllable, ERC20Upgradeable, IXMyrd {
    using EnumerableSet for EnumerableSet.AddressSet;

    //region ------------------------ Constants

    /// @inheritdoc IControllable
    string public constant VERSION = "1.0.0";

    /// @inheritdoc IXMyrd
    uint public constant BASIS = 10_000;

    /// @inheritdoc IXMyrd
    uint public constant SLASHING_PENALTY = 5000;

    /// @inheritdoc IXMyrd
    uint public constant MIN_VEST = 14 days;

    /// @inheritdoc IXMyrd
    uint public constant MAX_VEST = 180 days;

    // keccak256(abi.encode(uint256(keccak256("erc7201:myrd.XMyrd")) - 1)) & ~bytes32(uint256(0xff))
    bytes32 internal constant XMYRD_STORAGE_LOCATION = 0xf0ecfc0ccecc6975572b37fa830af6559e5857b63ecd7cc8ae9394138d3fd700; // erc7201:myrd.XMyrd
    //endregion ------------------------ Constants

    //region ------------------------ Data types

    /// @custom:storage-location erc7201:myrd.XMyrd
    struct MainStorage {
        /// @notice Myrd address
        address myrd;
        /// @notice Gauge to distribute rewards and control periods
        address gauge;
        /// @dev stores the addresses that are exempt from transfer limitations when transferring out
        EnumerableSet.AddressSet exempt;
        /// @dev stores the addresses that are exempt from transfer limitations when transferring to them
        EnumerableSet.AddressSet exemptTo;
        /// @notice Amount of pvp rebase penalties accumulated pending to be distributed
        uint pendingRebase;
        /// @notice The last period rebases were distributed
        uint lastDistributedPeriod;
        /// @notice returns info on a user's vests
        mapping(address => VestPosition[]) vestInfo;
    }
    //endregion ------------------------ Data types

    //region ------------------------ Initialization

    function initialize(address controller_, address myrd_, address gauge_) external initializer {
        __Controllable_init(controller_);
        __ERC20_init("xMyrd token", "XMYRD");
        MainStorage storage $ = _S();
        $.myrd = myrd_;
        $.gauge = gauge_;

        $.exempt.add(gauge_);
        $.exemptTo.add(gauge_);
    }
    //endregion ------------------------ Initialization

    //region ------------------------ Restricted actions

    /// @inheritdoc IXMyrd
    function rebase() external {
        MainStorage storage $ = _S();

        address _gauge = $.gauge;

        /// @dev gate to minter and call it on epoch flips
        if (msg.sender != _gauge) revert IAppErrors.NotGauge();

        /// @dev fetch the current period
        uint period = IGauge(_gauge).getPeriod();

        uint _pendingRebase = $.pendingRebase;

        /// @dev if it's a new period (epoch)
        if (
            /// @dev if the rebase is greater than the Basis
            period > $.lastDistributedPeriod && _pendingRebase >= BASIS
        ) {
            $.lastDistributedPeriod = period;

            /// @dev zero it out
            $.pendingRebase = 0;

            /// @dev Transfer MYRD to the gauge
            /// @dev Assume that the gauge expects such transfer and monitors changing of MYRD-balance
            IERC20($.myrd).transfer(_gauge, _pendingRebase);

            emit Rebase(msg.sender, _pendingRebase);
        }
    }

    /// @inheritdoc IXMyrd
    function setExemptionFrom(address[] calldata exemptee, bool[] calldata exempt) external {
        if (! isGovernance(msg.sender)) revert IAppErrors.NotGovernance(); // todo deployer (?)

        /// @dev ensure arrays of same length
        if (exemptee.length != exempt.length) revert IAppErrors.IncorrectArrayLength();

        MainStorage storage $ = _S();
        EnumerableSet.AddressSet storage exemptFrom = $.exempt;

        /// @dev loop through all and attempt add/remove based on status
        uint len = exempt.length;
        for (uint i; i < len; ++i) {
            bool success = exempt[i] ? exemptFrom.add(exemptee[i]) : exemptFrom.remove(exemptee[i]);
            /// @dev emit : (who, status, success)
            emit ExemptionFrom(exemptee[i], exempt[i], success);
        }
    }

    /// @inheritdoc IXMyrd
    function setExemptionTo(address[] calldata exemptee, bool[] calldata exempt) external {
        if (! isGovernance(msg.sender)) revert IAppErrors.NotGovernance(); // todo deployer (?)

        /// @dev ensure arrays of same length
        if (exemptee.length != exempt.length) revert IAppErrors.IncorrectArrayLength();

        MainStorage storage $ = _S();
        EnumerableSet.AddressSet storage exemptTo = $.exemptTo;

        /// @dev loop through all and attempt add/remove based on status
        uint len = exempt.length;
        for (uint i; i < len; ++i) {
            bool success = exempt[i] ? exemptTo.add(exemptee[i]) : exemptTo.remove(exemptee[i]);
            /// @dev emit : (who, status, success)
            emit ExemptionTo(exemptee[i], exempt[i], success);
        }
    }
    //endregion ------------------------ Restricted actions

    //region ------------------------ User actions

    /// @inheritdoc IXMyrd
    function enter(uint amount_) external {
        /// @dev ensure the amount_ is > 0
        if (amount_ == 0) revert IAppErrors.IncorrectZeroArgument();

        /// @dev transfer from the caller to this address
        // slither-disable-next-line unchecked-transfer
        IERC20(myrd()).transferFrom(msg.sender, address(this), amount_);

        /// @dev mint the xMyrd to the caller
        _mint(msg.sender, amount_);

        /// @dev Refresh user balance in the gauge
        IGauge(_S().gauge).handleBalanceChange(msg.sender);

        /// @dev emit an event for conversion
        emit Enter(msg.sender, amount_);
    }

    /// @inheritdoc IXMyrd
    function enterFor(uint amount_, address receiver) external {
        /// @dev ensure the amount_ is > 0
        if (amount_ == 0) revert IAppErrors.IncorrectZeroArgument();

        /// @dev ensure the receiver is not 0
        if (receiver == address(0)) revert IAppErrors.IncorrectZeroAddress();

        /// @dev transfer from the caller to this address
        // slither-disable-next-line unchecked-transfer
        IERC20(myrd()).transferFrom(msg.sender, address(this), amount_);

        /// @dev mint the xMyrd to the caller
        _mint(receiver, amount_);

        /// @dev Refresh user balance in the gauge
        IGauge(_S().gauge).handleBalanceChange(receiver);

        /// @dev emit an event for conversion
        emit Enter(receiver, amount_);
    }

    /// @inheritdoc IXMyrd
    function exit(uint amount_) external returns (uint exitedAmount) {
        /// @dev cannot exit a 0 amount
        if (amount_ == 0) revert IAppErrors.IncorrectZeroArgument();

        /// @dev if it's at least 2 wei it will give a penalty
        uint penalty = amount_ * SLASHING_PENALTY / BASIS;
        uint exitAmount = amount_ - penalty;

        /// @dev burn the xMyrd from the caller's address
        _burn(msg.sender, amount_);

        MainStorage storage $ = _S();

        /// @dev store the rebase earned from the penalty
        $.pendingRebase += penalty;

        /// @dev transfer the exitAmount to the caller
        // slither-disable-next-line unchecked-transfer
        IERC20($.myrd).transfer(msg.sender, exitAmount);

        /// @dev Refresh user balance in the gauge
        IGauge(_S().gauge).handleBalanceChange(msg.sender);

        /// @dev emit actual exited amount
        emit InstantExit(msg.sender, exitAmount);

        return exitAmount;
    }

    /// @inheritdoc IXMyrd
    function createVest(uint amount_) external {
        /// @dev ensure not 0
        if (amount_ == 0) revert IAppErrors.IncorrectZeroArgument();

        /// @dev preemptive burn
        _burn(msg.sender, amount_);

        MainStorage storage $ = _S();

        /// @dev fetch total length of vests
        uint vestLength = $.vestInfo[msg.sender].length;

        /// @dev push new position
        $.vestInfo[msg.sender].push(VestPosition(amount_, block.timestamp, block.timestamp + MAX_VEST, vestLength));

        /// @dev Refresh user balance in the gauge
        IGauge(_S().gauge).handleBalanceChange(msg.sender);

        emit NewVest(msg.sender, vestLength, amount_);
    }

    /// @inheritdoc IXMyrd
    function exitVest(uint vestID_) external {
        MainStorage storage $ = _S();

        VestPosition storage _vest = $.vestInfo[msg.sender][vestID_];
        if (_vest.amount == 0) revert NO_VEST();

        /// @dev store amount in the vest and start time
        uint _amount = _vest.amount;
        uint _start = _vest.start;

        /// @dev zero out the amount before anything else as a safety measure
        _vest.amount = 0;

        if (block.timestamp < _start + MIN_VEST) {
            /// @dev case: vest has not crossed the minimum vesting threshold
            /// @dev mint cancelled xMyrd back to msg.sender
            _mint(msg.sender, _amount);

            /// @dev Refresh user balance in the gauge
            IGauge(_S().gauge).handleBalanceChange(msg.sender);

            emit CancelVesting(msg.sender, vestID_, _amount);
        } else if (_vest.maxEnd <= block.timestamp) {
            /// @dev case: vest is complete
            /// @dev send liquid Myrd to msg.sender
            // slither-disable-next-line unchecked-transfer
            IERC20($.myrd).transfer(msg.sender, _amount);

            emit ExitVesting(msg.sender, vestID_, _amount, _amount);
        } else {
            /// @dev case: vest is in progress
            /// @dev calculate % earned based on length of time that has vested
            /// @dev linear calculations

            /// @dev the base to start at (50%)
            uint base = _amount * SLASHING_PENALTY / BASIS;

            /// @dev calculate the extra earned via vesting
            uint vestEarned = _amount * (BASIS - SLASHING_PENALTY) * (block.timestamp - _start) / MAX_VEST / BASIS;

            uint exitedAmount = base + vestEarned;

            /// @dev add to the existing pendingRebases
            $.pendingRebase += (_amount - exitedAmount);

            /// @dev transfer underlying to the sender after penalties removed
            // slither-disable-next-line unchecked-transfer
            IERC20($.myrd).transfer(msg.sender, exitedAmount);

            emit ExitVesting(msg.sender, vestID_, _amount, exitedAmount);
        }
    }
    //endregion ------------------------ User actions

    //region ------------------------ Views

    /// @inheritdoc IXMyrd
    function myrd() public view returns (address) {
        return _S().myrd;
    }

    /// @inheritdoc IXMyrd
    function gauge() external view returns (address) {
        return _S().gauge;
    }

    /// @inheritdoc IXMyrd
    function vestInfo(address user, uint vestID) external view returns (uint amount, uint start, uint maxEnd) {
        MainStorage storage $ = _S();
        VestPosition memory vestPosition = $.vestInfo[user][vestID];
        amount = vestPosition.amount;
        start = vestPosition.start;
        maxEnd = vestPosition.maxEnd;
    }

    /// @inheritdoc IXMyrd
    function usersTotalVests(address who) external view returns (uint numOfVests) {
        MainStorage storage $ = _S();
        return $.vestInfo[who].length;
    }

    /// @inheritdoc IXMyrd
    function pendingRebase() external view returns (uint) {
        return _S().pendingRebase;
    }

    /// @inheritdoc IXMyrd
    function lastDistributedPeriod() external view returns (uint) {
        return _S().lastDistributedPeriod;
    }
    //endregion ------------------------ Views

    //region ------------------------ Hooks to override

    function _update(address from, address to, uint value) internal override {
        if (!_isExempted(from, to)) revert NOT_WHITELISTED(from, to);

        /// @dev call parent function
        super._update(from, to, value);
    }
    //endregion ------------------------ Hooks to override

    //region ------------------------ Internal logic

    /// @dev internal check for the transfer whitelist
    function _isExempted(address from_, address to_) internal view returns (bool) {
        MainStorage storage $ = _S();
        return (from_ == address(0) || to_ == address(0) || $.exempt.contains(from_) || $.exemptTo.contains(to_));
    }

    function _S() internal pure returns (MainStorage storage $) {
        //slither-disable-next-line assembly
        assembly {
            $.slot := XMYRD_STORAGE_LOCATION
        }
        return $;
    }
    //endregion ------------------------ Internal logic
}
